:root{
  /* ===== Premium Light Palette (Ã¤hnlich Screenshot) ===== */
  --bg:#f6f7fb;                 /* App-Hintergrund */
  --panel:#ffffff;              /* Card-Panel */
  --text:#0f172a;               /* Haupttext */
  --muted:#64748b;              /* SekundÃ¤rtext */
  --line:rgba(15,23,42,.10);    /* Border */
  --shadow:0 14px 40px rgba(2,6,23,.08);

  --accent:#2563eb;             /* Blue (Button/Focus) */
  --accent-soft:rgba(37,99,235,.10);
  --accent-border:rgba(37,99,235,.25);

  --good:#16a34a;               /* Green */
  --good-soft:rgba(22,163,74,.12);
  --good-border:rgba(22,163,74,.28);

  --bad:#dc2626;                /* Red */
  --bad-soft:rgba(220,38,38,.10);
  --bad-border:rgba(220,38,38,.22);

  --radius:16px;
  --radius2:12px;
}

/* ===== Base ===== */
.card{
  background: var(--bg);
  color: var(--text);
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Inter, Roboto, Arial, sans-serif;
  font-size: 18px;
  line-height: 1.45;
}

/* ===== Main Container ===== */
.mcq{
  max-width: 980px;
  margin: 0 auto;
  padding: 18px;
  border: 1px solid var(--line);
  border-radius: var(--radius);
  background:
    linear-gradient(180deg, rgba(37,99,235,.06), rgba(255,255,255,0) 55%),
    var(--panel);
  box-shadow: var(--shadow);
}

/* ===== Topbar ===== */
.topbar{
  display:flex;
  gap:10px;
  align-items:center;
  margin-bottom: 12px;
}

.tag, .mode{
  font-size: 12px;
  letter-spacing: .10em;
  text-transform: uppercase;
  padding: 6px 10px;
  border-radius: 999px;
  border: 1px solid var(--line);
  background: rgba(15,23,42,.03);
  color: var(--muted);
}

.tag{
  border-color: var(--accent-border);
  background: var(--accent-soft);
  color: rgba(37,99,235,.92);
}

.spacer{ flex:1; }

/* ===== Question Stem ===== */
.stem{
  padding: 14px;
  border-radius: var(--radius2);
  border: 1px solid var(--line);
  background: rgba(15,23,42,.02);
  font-size: 20px;
  font-weight: 750;
}

.sr{ display:none; }

/* ===== Front options (radio) ===== */
.radioBox{
  margin-top: 14px;
  display:flex;
  flex-direction:column;
  gap:10px;
}

.rOpt{
  display:flex;
  align-items:flex-start;
  gap: 12px;
  padding: 12px;
  border-radius: var(--radius2);
  border: 1px solid var(--line);
  background: rgba(15,23,42,.02);  /* leichtes Grau wie im Screenshot */
  cursor: pointer;
  transition: background .15s ease, border-color .15s ease, transform .12s ease;
}

.rOpt input{
  margin-top: 6px;
  transform: scale(1.12);
  accent-color: var(--accent);
}

.k{
  width: 34px;
  height: 34px;
  display:flex;
  align-items:center;
  justify-content:center;
  border-radius: 10px;
  border: 1px solid var(--accent-border);
  background: var(--accent-soft);
  font-weight: 900;
  color: rgba(37,99,235,.92);
}

.t{
  font-weight: 520;
  color: rgba(15,23,42,.92);
}

/* Hover (front) */
.rOpt:hover{
  background: rgba(37,99,235,.06);
  border-color: rgba(37,99,235,.22);
  transform: translateY(-1px);
}

/* Selected (front) */
.rOpt.pick{
  border-color: rgba(37,99,235,.35) !important;
  background: rgba(37,99,235,.08) !important;
}

/* Feedback (front) */
.rOpt.true{ border-color: var(--good-border) !important; }
.rOpt.ok{
  border-color: rgba(22,163,74,.40) !important;
  background: var(--good-soft) !important;
}
.rOpt.no{
  border-color: rgba(220,38,38,.30) !important;
  background: var(--bad-soft) !important;
}

/* ===== Back options (list) ===== */
.opts{
  margin-top: 14px;
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.opt{
  display: grid !important;
  grid-template-columns: 44px 1fr;
  column-gap: 12px;
  align-items: start;

  padding: 12px;
  border-radius: 12px;
  border: 1px solid var(--line);
  background: rgba(15,23,42,.02);
  transition: background .15s ease, border-color .15s ease;
}

.opt .k{
  width: 34px;
  height: 34px;
  display:flex;
  align-items:center;
  justify-content:center;
  border-radius: 10px;
  border: 1px solid var(--accent-border);
  background: var(--accent-soft);
  font-weight: 900;
  color: rgba(37,99,235,.92);
}

.opt .t{
  display:block !important;
  white-space: normal !important;
  overflow-wrap: anywhere;
  word-break: normal;
  min-width: 0;
  font-weight: 520;
  color: rgba(15,23,42,.92);
  margin-top: 3px;
}

.opt .t br{ display:none; }

/* Hover (back) */
.opt:hover{
  background: rgba(15,23,42,.04);
  border-color: rgba(15,23,42,.14);
}

/* Back: correct highlight */
.opt.correct{
  border-color: rgba(22,163,74,.35) !important;
  background: rgba(22,163,74,.10) !important;
}
.opt.correct .k{
  border-color: rgba(22,163,74,.45) !important;
  background: rgba(22,163,74,.14) !important;
  color: rgba(12,74,30,.95) !important;
}

/* Back: show what you chose */
.opt.chosen-wrong{
  border-color: rgba(220,38,38,.30) !important;
  background: rgba(220,38,38,.08) !important;
}
.opt.chosen-wrong .k{
  border-color: rgba(220,38,38,.35) !important;
  background: rgba(220,38,38,.10) !important;
  color: rgba(127,29,29,.95) !important;
}
.opt.chosen-ok{
  border-color: rgba(22,163,74,.35) !important;
  background: rgba(22,163,74,.10) !important;
}
.opt.chosen-ok .k{
  border-color: rgba(22,163,74,.45) !important;
  background: rgba(22,163,74,.14) !important;
  color: rgba(12,74,30,.95) !important;
}

/* Optional: dim incorrect on back */
.dim{ opacity: .62; }

/* ===== Actions / Button ===== */
.actions{
  display:flex;
  align-items:center;
  gap: 12px;
  margin-top: 8px;
}

.btn{
  padding: 10px 14px;
  border-radius: 12px;
  border: 1px solid rgba(37,99,235,.28);
  background: linear-gradient(180deg, rgba(37,99,235,.18), rgba(37,99,235,.10));
  color: rgba(15,23,42,.92);
  font-weight: 800;
}
.status{
  color: var(--muted);
  font-size: 14px;
}

/* ===== Answer Bar (Back) ===== */
.answerBar, .answer{
  margin-top: 14px;
  display:flex;
  align-items:center;
  gap:10px;
  padding: 12px;
  border-radius: 12px;
  border: 1px solid var(--good-border);
  background: var(--good-soft);
}

.lbl{
  font-size: 12px;
  letter-spacing: .10em;
  text-transform: uppercase;
  padding: 4px 8px;
  border-radius: 999px;
  border: 1px solid var(--good-border);
  background: rgba(22,163,74,.10);
  color: rgba(12,74,30,.92);
}

.val{ font-weight: 900; }

/* ===== Explanation (Why) ===== */
.why{
  margin-top: 14px;
  padding: 16px 18px;
  border-radius: 12px;

  border: 1px dashed rgba(15,23,42,.12);
  border-left: 3px solid rgba(37,99,235,.28);

  background:
    radial-gradient(120% 100% at 0% 0%, rgba(37,99,235,.06), rgba(255,255,255,0) 70%),
    rgba(15,23,42,.015);

  font-size: 15.5px;
  line-height: 1.65;
  color: rgba(15,23,42,.78);
}

.why p{ margin: 0 0 8px 0; }
.why ul{ margin: 6px 0 2px 16px; padding:0; }
.why li{ margin: 3px 0; color: rgba(15,23,42,.76); }
.why li::marker{ color: rgba(37,99,235,.75); }

.why b{
  display:inline;
  margin: 0;
  font-size: 1em;
  letter-spacing: normal;
  text-transform: none;
  font-weight: 700;
  color: rgba(22,163,74,.92);
}


.why em{
  color: rgba(22,163,74,.92);
  font-style: normal;
  font-weight: 650;
}
 /* Spacing zwischen Correct-Box und TAGS */
.tagsRow{
  margin-top: 10px;      /* Abstand zur answerBar */
  padding-top: 2px;      /* optische Luft */
}

/* Optional: wenn du unten noch mehr Luft willst */
.answerBar{ margin-bottom: 6px; }

.prompt{
  margin-top: 12px;
  padding: 6px 2px;

  font-size: 12.5px;
  font-weight: 600;

  color: rgba(15,23,42,.58);
  background: transparent;
  border: none;

  border-left: 3px solid rgba(253, 230, 138, .75);
  padding-left: 10px;
}
/* ===== Make TAGS a subtle meta row (premium) ===== */
.tag.tagsRow{
  margin-top: 14px;
  padding: 6px 10px;

  font-size: 12.5px;
  font-weight: 600;
  letter-spacing: .04em;
  opacity: 0.9;

  border-radius: 999px;
  background: rgba(37,99,235,.08);
  border: 1px solid rgba(37,99,235,.22);
  color: rgba(37,99,235,.9);
}
.visual-link {
  margin-top: 8px;
  padding: 8px 10px;
  background: #f4f8ff;
  border-left: 3px solid #6b9cff;
  font-size: 0.95em;
}
.exam-trap {
  margin-top: 10px;
  padding: 8px 10px;
  background: #fff5f5;
  border-left: 3px solid #ff6b6b;
  font-size: 0.95em;
}
/* ===== Explanation (Why) â€“ soft green refinement ===== */
.why{
  margin-top: 14px;
  padding: 16px 18px;
  border-radius: 12px;

  border: 1px dashed rgba(22,163,74,.22);          /* soft green dash */
  border-left: 3px solid rgba(22,163,74,.45);      /* clear but calm */

  background:
    radial-gradient(
      120% 100% at 0% 0%,
      rgba(22,163,74,.08),
      rgba(255,255,255,0) 70%
    ),
    rgba(22,163,74,.035);                          /* faint green wash */

  font-size: 15.5px;
  line-height: 1.65;
  color: rgba(15,23,42,.78);
}

.why b{
  color: rgba(22,163,74,.92);
}

.why li::marker{ color: rgba(22,163,74,.75); }


.why-title{
  display:block;
  margin-top: 12px;
  margin-bottom: 6px;
  font-size: 13px;
  letter-spacing: .05em;
  text-transform: uppercase;
  font-weight: 700;
  color: rgba(22,163,74,.92);
}
.timeRow{
  margin: 10px 0 6px;
  padding: 10px 12px;
  border-radius: 12px;
  background: rgba(0,0,0,0.03);
  border: 1px solid rgba(0,0,0,0.06);
  color: rgba(0,0,0,0.70);
  font-size: 0.92em;
}
.timeRow b{ color: rgba(0,0,0,0.85); }
/* ===== Time-to-commit: speed coloring ===== */
.timeRow{
  margin: 10px 0 6px;
  padding: 10px 12px;
  border-radius: 12px;
  background: rgba(0,0,0,0.03);
  border: 1px solid rgba(0,0,0,0.06);
  color: rgba(0,0,0,0.70);
  font-size: 0.92em;

  /* new: subtle left accent that will change by speed */
  border-left: 3px solid rgba(15,23,42,.14);
}

.timeRow b{ color: rgba(0,0,0,0.85); }

/* Fast = green */
.timeRow.speed-fast{
  background: var(--good-soft);
  border-color: var(--good-border);
  border-left-color: rgba(22,163,74,.55);
}
.timeRow.speed-fast b{ color: rgba(12,74,30,.92); }

/* Medium = blue (calm) */
.timeRow.speed-mid{
  background: var(--accent-soft);
  border-color: var(--accent-border);
  border-left-color: rgba(37,99,235,.50);
}
.timeRow.speed-mid b{ color: rgba(37,99,235,.92); }

/* Slow = amber (warning) */
.timeRow.speed-slow{
  background: rgba(253,230,138,.22);
  border-color: rgba(253,230,138,.55);
  border-left-color: rgba(245,158,11,.70);
}
.timeRow.speed-slow b{ color: rgba(180,83,9,.95); }

/* Very slow / near limit = red (danger) */
.timeRow.speed-danger{
  background: var(--bad-soft);
  border-color: var(--bad-border);
  border-left-color: rgba(220,38,38,.55);
}
.timeRow.speed-danger b{ color: rgba(127,29,29,.95); }
